    var selectedPath: String = eightNotePathData
        set(value) {
            field = value
            stopTimer()
            binding.fourierView.clear()
            createPaths()
        }

    var scaleValue = 10f

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)

        binding = ActivityMainBinding.inflate(layoutInflater)

        setContentView(binding.root)

        binding.slider.valueTo = 200f
        binding.slider.valueFrom = 2f
        binding.slider.value = numberOfCircles
        binding.slider.addOnSliderTouchListener(object : Slider.OnSliderTouchListener {
            override fun onStartTrackingTouch(slider: Slider) {

            }

            override fun onStopTrackingTouch(slider: Slider) {
                numberOfCircles = slider.value
            }
        })

        binding.musicNote.setOnClickListener {
            scaleValue = 10f
            selectedPath = eightNotePathData
        }

        binding.italy.setOnClickListener {
            scaleValue = 2f
            selectedPath = italy
        }

        binding.usa.setOnClickListener {
            scaleValue = 1f
            selectedPath = usa
        }

        binding.nike.setOnClickListener {
            scaleValue = 1f
            selectedPath = nikePathData
        }

        binding.square.setOnClickListener {
            scaleValue = 5f
            selectedPath = squarePathData
        }

        binding.mcdonalds.setOnClickListener {
            scaleValue = 5f
            selectedPath = mcdonaldsPathData
        }

        binding.cat.setOnClickListener {
            scaleValue = 1f
            selectedPath = cat
        }

        binding.fourierView.doOnLayout {
            createPaths()
        }

    }

    fun createPaths() {
        val deltaL = 0.0001f
        val pointList = createPathPointsFromPathData(selectedPath, deltaL)

        val width = binding.fourierView.measuredWidth
        val height = binding.fourierView.measuredHeight

        val complexPointList = pointList.toComplexList(width, height)

        val coeffs: Map<Int, Complex> =
            calculateCoefficients(complexPointList, numberOfCircles.toInt(), deltaL)

        // create ellipsoids
        val firstCoeff = coeffs[0]!!.toPolar()
        val firstEllipsoid =
            Ellipsoid(width / 2f, height / 2f, 0f, firstCoeff.second, firstCoeff.first)

        val ellipsoidSet = mutableSetOf<Ellipsoid>(firstEllipsoid)

        var lastEllipsoid = firstEllipsoid

        for (i in 1..coeffs.size / 2) {
            val posCoeff = coeffs[i]!!.toPolar()
            val lastEllipsoidXY = lastEllipsoid.getArrowEnd()
            val posEllipsoid = Ellipsoid(
                lastEllipsoidXY.first,
                lastEllipsoidXY.second,
                i.toFloat(),
                posCoeff.second,
                posCoeff.first,
                prev = lastEllipsoid
            )
            ellipsoidSet.add(posEllipsoid)

            val negCoef = coeffs[-i]!!.toPolar()
            val posEllipsoidXY = posEllipsoid.getArrowEnd()
            val negEllipsoid = Ellipsoid(
                posEllipsoidXY.first,
                posEllipsoidXY.second,
                -i.toFloat(),
                negCoef.second,
                negCoef.first,
                prev = posEllipsoid
            )
            ellipsoidSet.add(negEllipsoid)

            lastEllipsoid = negEllipsoid

        }

        lastEllipsoid.draw = true


        startJob(ellipsoidSet)
    }

    private fun calculateCoefficients(
        complexPointList: List<Complex>,
        n: Int,
        deltaT: Float
    ): Map<Int, Complex> {
        var i = -n / 2
        val retMap = mutableMapOf<Int, Complex>()
        while (i <= n / 2) {
            var summation: Complex = ZERO
            for (j in complexPointList.indices) {
                summation += fourierValue(complexPointList[j], j * deltaT, deltaT, i)
            }
            retMap[i] = summation

            i += 1
        }

        return retMap
    }

    fun List<Pair<Float, Float>>.toComplexList(width: Int, height: Int): List<Complex> = map {
        Pair(it.first - width / 2f, height / 2f - it.second).toComplex()
    }

    fun stopTimer() {
        job?.cancel()
        job = null
    }

    fun startJob(ellipsoidSet: MutableSet<Ellipsoid>) {
        job = tickerFlow(deltaT)
            .onEach {
                ellipsoidSet.forEach {
                    it.rotate(deltaT)

                    if (it.prev != null) {
                        val prevArrowEnd = it.prev!!.getArrowEnd()
                        it.x = prevArrowEnd.first
                        it.y = prevArrowEnd.second
                    }
                }
                binding.fourierView.updateDataSet(ellipsoidSet)
            }.launchIn(GlobalScope)
    }

    fun createPathPointsFromPathData(
        pathData: String,
        deltaL: Float = 0.01f
    ): List<Pair<Float, Float>> {
        val path = PathParser.createPathFromPathData(pathData) ?: return listOf()

        normalizePoints(binding.fourierView.measuredWidth, binding.fourierView.measuredHeight, path)

        val pathMeasure = PathMeasure(path, true)

        val aCoordinates = floatArrayOf(0f, 0f)

        var i = 0f

        val pathPointList = mutableListOf<Pair<Float, Float>>()

        while (i < 1 - deltaL) {
            pathMeasure.getPosTan(pathMeasure.length * i, aCoordinates, null)

            pathPointList.add(Pair(aCoordinates[0], aCoordinates[1]))

            i += deltaL
        }

        return pathPointList
    }

    private fun normalizePoints(width: Int, height: Int, path: Path) {
        val pathSizeRect = RectF()

        val scaleMatrix = Matrix()
        scaleMatrix.setScale(scaleValue, scaleValue)
        path.transform(scaleMatrix)

        path.computeBounds(pathSizeRect, true)

        scaleMatrix.setTranslate(
            width / 2f - ((pathSizeRect.right - pathSizeRect.left) / 2f),
            height / 2f - ((pathSizeRect.bottom - pathSizeRect.top) / 2f)
        )
        path.transform(scaleMatrix)
    }
}

fun tickerFlow(periodMillis: Long) = flow {
    while (true) {
        emit(Unit)
        delay(periodMillis)
    }
}
